import {useState} from "react";
import {useHistory} from "react-router-dom";
import '../node_modules/bootstrap/dist/css/bootstrap.css'

function Login(props)
{
    var [user,setUser] = useState({username: "", password: ""});
    var [message,setMessage] = useState("");
    var history = useHistory();

    var TextChanged = function(args)
    {
        var copyOfUser = {...user};
        copyOfUser[args.target.name] = args.target.value;
        setUser(copyOfUser);
    }

    var Login = function()
    {
        if(user.username === "aharta" && user.password === "aharta")
        {
            sessionStorage.setItem("userLogedIn","true");
            sessionStorage.setItem("username",user.username);
            if(props.setUser!=null)
            {
                props.setuser(user.username);
            }
            if(props.setUser!=null && props.path!="/login")
            {
                history.push(props.path);
            }
            else
            {
                history.push("/home");
            }
        }
        else
        {
            setUser({username:"",password:""});
            setMessage("Invalid Credentials");
        }
    }

    return<>    
        <center>
            <div className="table-responsive" style={{width: 400}}>
                <table className="table table-bordered">
                    <tbody>
                        <tr>
                            <td>User Name</td>
                            <td>
                                <input type="text"
                                value={user.username}
                                onChange={TextChanged}
                                name="username"/>
                            </td>
                        </tr>
                        <tr>
                            <td>Password</td>
                            <td>
                                <input type="password"
                                value={user.password}
                                onChange={TextChanged}
                                name="password"/>
                            </td>
                        </tr>
                        <tr>
                            <button className="btn btn-Primary" onClick={Login} >Sign In</button>
                        </tr>
                    </tbody>
                </table>

            </div>
            <div>
                {message}
            </div>
        </center>
        </>
}

export default Login;
