import {useState} from "react";
import {useHistory} from "react-router-dom";
import '../node_modules/bootstrap/dist/css/bootstrap.css'
import axios from "axios";

function Login(props)
{
    var [owner,setOwner] = useState({owner_email: "", owner_password: ""});
    var [own, setOwn] = useState({owner_id:"", owner_first_name:""});
    var [message,setMessage] = useState("");
    var history = useHistory();

    var TextChanged =(args)=>
    {
        debugger;
        var copyOfOwner = {...owner};
        debugger;
        copyOfOwner[args.target.name] = args.target.value;
        setOwner(copyOfOwner);
        console.log(copyOfOwner)
    }

    var Login = () =>
    {
        debugger;
        axios.post("http://localhost:5000/owner/ownerlogin",owner).then((response)=>{
            debugger;
            console.log(response)
            if(response.data.data.valid.isValid === "true")
            {
                owner_id = response.data.data.owner_id
                debugger;
                console.log(response)
                sessionStorage.setItem("isOwnerLoggedIn","true");
                sessionStorage.setItem("owner_id",owner_id);
                history.push("/home");
            }
            else{
                setMessage("Invalid Credentials")
            }
            console.log(response);
        })
    }


    return<>    
        <center>
            <div className="table-responsive" style={{width: 400}}>
                <table className="table table-bordered">
                    <tbody>
                        <tr>
                            <td>User Name</td>
                            <td>
                                <input type="text"
                                onChange = {TextChanged}
                                id = "editUsername"
                                name="owner_email"/>
                            </td>
                        </tr>
                        <tr>
                            <td>Password</td>
                            <td>
                                <input type="password"
                                onChange = {TextChanged}
                                id = "editPassword"
                                name = "owner_password"/>
                            </td>
                        </tr>
                        <tr>
                            <button className="btn btn-primary" type = "button" onClick={Login} >Sign In</button>
                        </tr>
                    </tbody>
                </table>

            </div>
            <div>
                {message}
            </div>
        </center>
        </>
}

export default Login;
