                    setMessage("Record removed Successfully!!");
                    Select()
                    props.setUser();
                }
            }
        }
        helper.open("DELETE","http://localhost:5000/admin/deleteowner/" + owner_id);
        helper.send();
    }


    return(
        <>
                    {pg.map((p)=>{     
                        return(    
                    <div class="card" style={{marginLeft:400, marginRight:400,width:500}}>
                    <img src="https://rook.gumlet.io/uploads/center/cover_photo/5d91ae349540da537a932946/PG_in_Koramangala.jpg?format=webp&h=212&compress=true" class="card-img-top" alt="..."/>
                    <div class="card-body">
                        <h5 class="card-title">Name: {p.pg_name}</h5>
                        <h10 class="card-text">City: {p.pg_city}</h10>
                        <h10 class="card-text">pg_id: {p.pg_id}</h10>
                        <hr size="10" width="100%" color="red"></hr>
                        <span class="material-symbols-outlined">Rent: â‚¹{p.pg_rent}</span>
                        <h7></h7>
                        <button type="button" class="btn btn-info" onClick={()=>{
                            viewpg({
                                pg_id : p.pg_id,
                                pg_name : p.pg_name,
                                pg_type : p.pg_type,
                                pg_city : p.pg_city,
                                pg_isVacaent : p.pg_isVacaent,
                                pg_address : p.pg_address,
                                pg_rent : p.pg_rent,
                                pg_foodType : p.pg_foodType
                            })
                        }}>
                            ViewPg
                        </button><br/>
                        <button type="button" class="btn btn-danger" on>Delete</button>
                    </div>
                    </div>

                    )}
        )} 


        </>
    )
}

export default DisplayPg;
