import {useEffect, useState} from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.css';

function UpdateOwner()
{
    var [owner, setAdmin] = useState([]);
    var [message, setMessage] = useState("");
    var [adm, setAdm] = useState({admin_first_name:"",admin_last_name:"",admin_email:"",admin_address:"",admin_password:"",admin_mobile:""});
    var [operation, setOperation] = useState("");
    var [searchText, setSearchText] = useState("");

    useEffect(()=>{
        Select();
    }, []);


    var Select = function()
    {

        var helper = new XMLHttpRequest();

        helper.onreadystatechange =()=>{
            if(helper.readyState == 4 && helper.status == 200)
            {
                var result = JSON.parse(helper.responseText);
                setAdmin(result.data);
            }
        }
        helper.open("GET", "http://localhost:4002/admin/viewowner");
        helper.send();
    }

    var TextChanged = function(args)
    {
        var copyOfAdmin = {...adm};
        copyOfAdmin[args.target.name] = args.target.value;
        setAdm(copyOfAdmin);
    }


    var Update =function(){
        //Here this.state.emp will have updated record.
        //use this to send data using Put request to the server

        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{

            if(helper.readyState == 4 && helper.status == 200)
            {
                debugger;
                var result = JSON.parse(helper.responseText);
                if(result.affectedRows > 0)
                {
                    setMessage("Record Updated successfully!!");

                    Select();

                    setOwn({admin_first_name:"",admin_last_name:"",owner_email:"",owner_password:"",owner_mobile:""});
                }

            }
        }

        debugger;
        helper.open("PUT", "http://localhost:9999/emps/"+ emp.ENo);

        helper.setRequestHeader("Content-Type", "application/json");

        var dataToBePassedInStringFormat = JSON.stringify(emp );
        helper.send(dataToBePassedInStringFormat);
    }


    return(
        <>

        <table align='center'>
            <tbody>
                <tr>
                    <td>Admin_First_Name:</td>
                    <td>
                        <input type='text' name='owner_first_name' value={adm.admin_first_name} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>Admin_Last_Name:</td>
                    <td>
                        <input type='text' name='owner_last_name' value={adm.admin_last_name} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>Admin_Email:</td>
                    <td>
                        <input type='text' name='owner_email' value={own.owner_email} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>Admin_Password:</td>
                    <td>
                        <input type='text' name='owner_password' value={own.owner_password} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>Admin_Address:</td>
                    <td>
                        <input type='text' name='owner_password' value={own.owner_password} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>Admin_Mobile:</td>
                    <td>
                        <input type='text' name='owner_mobile' value={own.owner_mobile} onChange={TextChanged}></input>
                    </td>
                </tr>

            </tbody>
        </table>






        <div className='table-responsive'>
            {message}
            <table className='table table-bordered'>
                <thead>
                    <th>owner_id</th>
                    <th>owner_first_name</th>
                    <th>owner_last_name</th>
                    <th>owner_email</th>
                    <th>owner_password</th>
                    <th>owner_mobile</th>
                </thead>
                <tbody>
                 {
                    owner.map((own)=>{
                        return <tr>
                            <td>{own.owner_id}</td>
                            <td>{own.owner_first_name}</td>
                            <td>{own.owner_last_name}</td>
                            <td>{own.owner_email}</td>
                            <td>{own.owner_password}</td>
                            <td>{own.owner_mobile}</td>
                            <td>
                                <button className='btn btn-warning' onClick={Update}>
                                    UpdateOwner
                                </button>
                            </td>
                    </tr>
                    })
                }
                </tbody>
            </table>

        </div>

        </>
    )
}

export default UpdateOwner;
