import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './viewpgdetails.css'; // Import your custom CSS file

export default function ViewPgDetails() {
    const [pg, setPg] = useState([]);
    const location = useLocation();
    const history = useHistory();
    const pg_id = location.state;

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/user/viewpgbyid/${pg_id}`);
            setPg(response.data.data);
            console.log("Fetched data:", response.data);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    const getImg = (img) => {
        return `http://localhost:5000/${img}`;
    };

    const user_id = sessionStorage.getItem("user_id");

    const bookPg = (user_id, owner_id, pg_id) => {
        const obj = { user_id, owner_id, pg_id };
        axios.post("http://localhost:5000/user/bookpg", obj)
            .then((result) => {
                console.log("Booking successful:", result.data);
                toast.success("Booking successful");
                // history.push("/bookpg");
            })
            .catch((error) => {
                console.error("Error booking:", error);
                toast.error("Error booking the PG");
            });
    };

    const contactOwner = (ownerId) => {
        history.push({
            pathname: "/contactowner",
            state: ownerId
        });
    };

    return (
        <div className="pg-details-container">
            <h1 className="page-title">This is Pg details Page</h1>
            {pg.map((p) => (
                <div key={p.pg_id} className="card pg-card">
                    <img src={getImg(p.pg_Image)} className="card-img-top" alt="..." />
                    <div className="card-body">
                        <h5 className="card-title">Name: {p.pg_name}</h5>
                        <p className="card-text">City: {p.pg_city}</p>
                        {/* Add more card details as needed */}
                        <hr className="card-divider" />
                        <span className="rent-label">Rent: â‚¹{p.pg_rent}</span>
                        <button className="btn btn-outline-warning" onClick={() => contactOwner(p.owner_id)}>
                            Contact Owner
                        </button>
                        <button class="btn btn-outline-info" onClick={() => bookPg(user_id, p.owner_id, p.pg_id)}>
                            Book Pg
                        </button>
                    </div>
                </div>
            ))}
            <ToastContainer />
        </div>
    );
}
