import {useEffect, useState} from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.css';
import axios from 'axios';

function RegisterUser()
{
    var [owner, setOwner] = useState([]);
    var [message, setMessage] = useState("");
    var [user, setUser] = useState({user_first_name:"",user_last_name:"",user_email:"",user_password:"",user_mobile:""});


    useEffect(()=>{
        Select();
    }, []);

    var Select=()=>{
        axios.get("http://localhost:5000/admin/viewowner").then((result)=>{
            setOwner(result.data.data);
            console.log(result)
        })
    }

    var TextChanged = function(args)
    {
        var copyOfUser = {...user};
        copyOfUser[args.target.name] = args.target.value;
        setUser(copyOfUser);
    }

    var Insert = function()
    {
        var helper = new XMLHttpRequest();
        helper.onreadystatechange=()=>{

            if(helper.readyState == 4 && helper.status == 200)
            {
                var result = JSON.parse(helper.responseText);
                if(result.data.affectedRows > 0)
                {
                    setMessage("Record Added Successfully !!");
                    Select();
                    setUser({user_first_name:"",user_last_name:"",user_email:"",user_password:"",user_mobile:""});
                }
            }
        }

        helper.open("POST", "http://localhost:5000/owner/ownerRegistration");

        helper.setRequestHeader("Content-Type", "application/json");

        var dataToBePassedInStringFormat = JSON.stringify(own);
        helper.send(dataToBePassedInStringFormat);
    }


    return(
        <>
        <h1 align="center">User Registration Page</h1>
        <table align='center'>
            <tbody>
                <tr>
                    <td>User_First_Name:</td>
                    <td>
                        <input type='text' name='user_first_name' value={user_first_name} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>User_Last_Name:</td>
                    <td>
                        <input type='text' name='user_last_name' value={user_last_name} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>User_Email:</td>
                    <td>
                        <input type='text' name='user_email' value={user_email} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>User_Password:</td>
                    <td>
                        <input type='text' name='user_password' value={user_password} onChange={TextChanged}></input>
                    </td>
                </tr>

                <tr>
                    <td>User_Mobile:</td>
                    <td>
                        <input type='text' name='_mobile' value={user_mobile} onChange={TextChanged}></input>
                    </td>
                </tr>
                <tr >

                    <td align='center' colSpan={2}>
                <button  className='btn btn-success' onClick={Insert}>
                                    Register
                </button></td>

                </tr>
            </tbody>
        </table>

        </>
    )
}


export default RegisterUser;
